INSERT INTO campuses (name,address,description,image,primarycolor,secondarycolor) VALUES ('Radiant Springs University', '890 Imaginary Street, Illusionville, 1LLUS10NV1LL3', 'Celebrating creativity and artistic expression, this college offers comprehensive programs in visual arts, design, fashion, and performing arts. It provides students with the necessary tools and mentorship to pursue their artistic passions.', './pictures/7.jpg','#BF211E','#E9CE2C');
